import { useState } from "react";
import { ILanguageRelease } from "../models/interfaces";
import Markdown from "../components/Markdown";
import { DropdownString } from "../components/Dropdown";
import pb, { Tables } from "../models/pb";
import Device from "../components/Device";

export async function getServerSideProps() {
	const result = await pb
		.collection(Tables.releases)
		.getFullList<ILanguageRelease>({ sort: "-releaseDate" });
	return {
		props: {
			latestRelease: result,
		},
	};
}

function Artefact(props: {
	value: {
		id: string;
		name: string;
		icon: string;
		downloads: number;
		platform: string;
		architecture: string;
		target: string;
		size: string;
		path: string;
	};
	isDetected: boolean;
	releaseItemID: string;
	index: number;
	artefactCount: number;
}) {
	return (
		<div
			className="flex flex-col font-mono justify-start text-left border-2 border-solid border-midGray rounded-2xl shadow-lg dark:shadow-none bg-white dark:bg-black"
			key={props.index}
		>
			<div className="flex flex-row p-4 gap-4">
				{props.value.icon === "debian" ? (
					<DebianIcon />
				) : props.value.icon === "windows" ? (
					<WindowsIcon />
				) : props.value.icon === "linux" ? (
					<LinuxIcon />
				) : props.value.icon === "arch" ? (
					<ArchIcon />
				) : props.value.icon === "macos" ? (
					<MacIcon />
				) : (
					<></>
				)}
				<div className="flex flex-col">
					<div className="mb-1 sm:mb-2 font-bold text-base sm:text-lg">
						{props.value.name}
					</div>
					<p>{props.value.target}</p>
				</div>
			</div>
			<div className="flex-grow" />
			<div
				className="text-white text-xl w-full px-3 py-2 font-bold select-none flex flex-row bg-styleGreen rounded-b-xl align-middle justify-start cursor-pointer hover:bg-black dark:hover:bg-white hover:text-white dark:hover:text-black active:bg-gray-400 border-t-[5px] border-solid border-transparent"
				style={{ borderColor: props.isDetected ? "#fec033" : undefined }}
				onClick={() => {
					window.open(props.value.path, "_blank");
				}}
			>
				Download
				<div className="flex-grow" />
				<p className="text-base self-center">{props.value.size}</p>
			</div>
		</div>
	);
}

export default function Downloads(props: {
	latestRelease: ILanguageRelease[];
}) {
	const [showNotes, setShowNotes] = useState<boolean>(true);
	const [release, setRelease] = useState<number>(0);
	const [platform, setPlatform] = useState<string | null>(null);
	return (
		<div className="flex flex-col w-full h-full py-2 px-12 overflow-y-auto">
			<title>Downloads | QAT Programming Language</title>
			<div className="w-72">
				<DropdownString
					name="Version"
					items={props.latestRelease.map((r) => {
						return {
							name:
								r.version.value +
								(r.version.isPrerelease
									? " " + r.version.prerelease
									: ""),
							value: r.version.value,
						};
					})}
					onChange={(_, ind) => {
						setRelease(ind!);
					}}
					default={props.latestRelease[0].version.value}
					disallowNone
				/>
			</div>
			<Device>
				{({ isWindows, isMacOs, isDesktop }) => {
					if (isWindows) {
						setPlatform("windows");
					} else if (isMacOs) {
						setPlatform("macOS");
					} else if (isDesktop) {
						setPlatform("linux");
					}
					return <></>;
				}}
			</Device>
			<div className="font-bold font-mono text-xl text-left my-2">
				{props.latestRelease[release].title}...
			</div>
			<div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 2xl:grid-cols-4 gap-4 mt-2 mb-6">
				{props.latestRelease[release].files
					.sort(
						(a, b) =>
							(a.platform === platform ? 0 : 10) -
							(b.platform === platform ? 0 : 10),
					)
					.flatMap((artefact, i) => {
						return (
							<Artefact
								releaseItemID={props.latestRelease[release].id}
								value={artefact}
								index={i}
								isDetected={artefact.platform === platform}
								artefactCount={
									props.latestRelease[release].files.length
								}
							/>
						);
					})}
			</div>
			<Markdown
				className="text-sm"
				children={
					"For installation instructions, visit the [documentation](/docs)"
				}
			/>
			<div
				className="mt-2 font-mono font-bold tracking-widest cursor-pointer select-none w-fit px-4 py-2 bg-[#eeeeee] hover:bg-[#dddddd] dark:bg-[#ffffff22] dark:hover:bg-[#ffffff33] rounded-lg"
				style={{
					borderBottomLeftRadius: showNotes ? "0px" : undefined,
					borderBottomRightRadius: showNotes ? "0px" : undefined,
				}}
				onClick={() => {
					setShowNotes(!showNotes);
				}}
			>
				RELEASE NOTES {showNotes ? "-" : "+"}
			</div>
			{showNotes && (
				<Markdown
					className="bg-[#eeeeee] dark:bg-[#ffffff22] p-4 rounded-lg rounded-tl-none mb-10"
					children={props.latestRelease[release].content}
				/>
			)}
		</div>
	);
}

